import React, { useState, useEffect } from 'react';
import './App.css';
function FinancialData() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortBy, setSortBy] = useState('revenue'); // Default sort by revenue
  const [order, setOrder] = useState('asc'); // Default sort order

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/data?sort_by=${sortBy}&order=${order}`);
        const result = await response.json();
        console.log("Fetched data:", result); // Log the data to see its structure
        setData(result);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
  
    fetchData();
  }, [sortBy, order]); // Re-fetch data when sortBy or order changes
   // Re-fetch data when sortBy or order changes

  // Handle sorting on column click
  const handleSort = (column) => {
    if (sortBy === column) {
      setOrder(order === 'asc' ? 'desc' : 'asc'); // Toggle order when the same column is clicked
    } else {
      setSortBy(column); // Set new column to sort by
      setOrder('asc'); // Reset to ascending order by default
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">Error: {error.message}</div>;
  }

  return (
    <div className="data-container">
      <h1 className="title">Annual Financial Data for Apple</h1>
      <table className="data-table">
        <thead>
          <tr>
            <th onClick={() => handleSort('date')}>Date</th>
            <th onClick={() => handleSort('revenue')}>Revenue</th>
            <th onClick={() => handleSort('netIncome')}>Net Income</th>
            <th onClick={() => handleSort('grossProfit')}>Gross Profit</th>
            <th onClick={() => handleSort('eps')}>EPS (Earnings Per Share)</th>
            <th onClick={() => handleSort('operatingIncome')}>Operating Income</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.date}</td>
              <td>{item.revenue}</td>
              <td>{item.netIncome}</td>
              <td>{item.grossProfit}</td>
              <td>{item.eps}</td>
              <td>{item.operatingIncome}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FinancialData;
