import React, { useState, useEffect } from "react";
import "./App.css";

function FinancialData() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortBy, setSortBy] = useState('revenue');
  const [order, setOrder] = useState('asc');

  useEffect(() => {
    const fetchData = async () => {
      try 
      {
        const response = await fetch(`http://localhost:5000/api/data?sort_by=${sortBy}&order=${order}`);
        const result = await response.json();
        console.log("Fetched data:", result);
        setData(result);
      } 
      catch (error) 
      {
        setError(error);
      } 
      finally 
      {
        setLoading(false);
      }
    };

    fetchData();
  }, [sortBy, order]);
  const handleSort = (column) => {
    if (sortBy === column) 
    {
      setOrder(order === 'asc' ? 'desc' : 'asc');
    } 
    else
    {
      setSortBy(column);
      setOrder('asc');
    }
  };

  if (loading) 
  {
    return <div className="loading">Loading...</div>;
  }

  if (error)
  {
    return <div className="error">Error: {error.message}</div>;
  }

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-800 text-center mb-8">Annual Financial Data for Apple</h1>
      <table className="table-auto w-full border border-gray-200 shadow-lg">
        <thead className="bg-gray-100">
          <tr>
            <th onClick={() => handleSort('date')}
              className="px-4 py-2 border border-gray-700">Date</th>
            <th onClick={() => handleSort('revenue')}
              className="px-4 py-2 border border-gray-700">Revenue</th>
            <th onClick={() => handleSort('netIncome')}
              className="px-4 py-2 border border-gray-700">Net Income</th>
            <th onClick={() => handleSort('grossProfit')}
              className="px-4 py-2 border border-gray-700">Gross Profit</th>
            <th onClick={() => handleSort('eps')}>EPS (Earnings Per Share)</th>
            <th onClick={() => handleSort('operatingIncome')}
              className="px-4 py-2 border border-gray-700">Operating Income</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.date}</td>
              <td>{item.revenue}</td>
              <td>{item.netIncome}</td>
              <td>{item.grossProfit}</td>
              <td>{item.eps}</td>
              <td>{item.operatingIncome}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FinancialData;
