import React, { useState, useEffect } from "react";

function FinancialData() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch data from the Flask backend
  const fetchData = async (sort, order, filter, min, max) => {
    setLoading(true);
    let url = `http://localhost:5000/data?`;
    if (sort) url += `sort=${sort}&`;
    if (order) url += `order=${order}&`;
    if (filter) url += `filter=${filter}&`;
    if (min) url += `min=${min}&`;
    if (max) url += `max=${max}&`;

    try {
      const response = await fetch(url);
      const result = await response.json();
      if (response.ok) {
        setData(result);
      } else {
        throw new Error(result.message || "Failed to fetch data");
      }
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  // Initial data fetch
  useEffect(() => {
    fetchData();
  }, []);

  // Handle sorting by column
  const handleSort = (column) => {
    const isAsc = data[0]?.[column] === data.sort((a, b) => a[column] - b[column])[0]?.[column];
    fetchData(column, isAsc ? "desc" : "asc");
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="container">
      <h1>Annual Financial Data for Apple</h1>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort("date")}>Date</th>
            <th onClick={() => handleSort("revenue")}>Revenue</th>
            <th onClick={() => handleSort("netIncome")}>Net Income</th>
            <th onClick={() => handleSort("grossProfit")}>Gross Profit</th>
            <th onClick={() => handleSort("eps")}>EPS</th>
            <th onClick={() => handleSort("operatingIncome")}>Operating Income</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.date}</td>
              <td>{item.revenue}</td>
              <td>{item.netIncome}</td>
              <td>{item.grossProfit}</td>
              <td>{item.eps}</td>
              <td>{item.operatingIncome}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FinancialData;
