import React, { useState, useEffect } from "react";

function FinancialData() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch data with optional parameters
  const fetchData = async (sort, order) => {
    setLoading(true);
    try {
      const response = await fetch(
        `http://localhost:5000/data?sort=${sort || ""}&order=${order || ""}`
      );
      const result = await response.json();
      setData(result);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch initial data
  useEffect(() => {
    fetchData();
  }, []);

  // Sorting handler
  const handleSort = (column) => {
    const isAsc = data[0]?.[column] === data.sort((a, b) => a[column] - b[column])[0]?.[column];
    fetchData(column, isAsc ? "desc" : "asc");
  };

  if (loading) {
    return (
      <div className="text-center text-gray-600 text-lg mt-6">
        Loading...
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center text-red-500 text-lg mt-6">
        Error: {error.message}
      </div>
    );
  }

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-800 text-center mb-8">
        Annual Financial Data for Apple
      </h1>
      <div className="overflow-x-auto">
        <table className="table-auto w-full border border-gray-200 shadow-lg">
          <thead className="bg-gray-100">
            <tr>
              <th
                className="px-4 py-2 border border-gray-300 cursor-pointer"
                onClick={() => handleSort("date")}
              >
                Date
              </th>
              <th
                className="px-4 py-2 border border-gray-300 cursor-pointer"
                onClick={() => handleSort("revenue")}
              >
                Revenue
              </th>
              <th
                className="px-4 py-2 border border-gray-300 cursor-pointer"
                onClick={() => handleSort("netIncome")}
              >
                Net Income
              </th>
              <th
                className="px-4 py-2 border border-gray-300 cursor-pointer"
                onClick={() => handleSort("grossProfit")}
              >
                Gross Profit
              </th>
              <th
                className="px-4 py-2 border border-gray-300 cursor-pointer"
                onClick={() => handleSort("eps")}
              >
                EPS
              </th>
              <th
                className="px-4 py-2 border border-gray-300 cursor-pointer"
                onClick={() => handleSort("operatingIncome")}
              >
                Operating Income
              </th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr
                key={index}
                className="odd:bg-white even:bg-gray-50 hover:bg-gray-100"
              >
                <td className="px-4 py-2 border border-gray-300">
                  {item.date}
                </td>
                <td className="px-4 py-2 border border-gray-300">
                  {item.revenue}
                </td>
                <td className="px-4 py-2 border border-gray-300">
                  {item.netIncome}
                </td>
                <td className="px-4 py-2 border border-gray-300">
                  {item.grossProfit}
                </td>
                <td className="px-4 py-2 border border-gray-300">
                  {item.eps}
                </td>
                <td className="px-4 py-2 border border-gray-300">
                  {item.operatingIncome}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default FinancialData;
